query GetSubsectionActivity($moduleUniqueCode: String!, $subsectionUniqueCode: String!, $activityUniqueCode: String) {
  subsection(
    filter: {uniqueCode: $subsectionUniqueCode, moduleUniqueCode: $moduleUniqueCode}
  ) {
    id
    name
    uniqueCode
    module {
      id
      title
      subtitle
      uniqueCode
      currentUserEnrolment {
        id
        isTrial
        isActive
        purchases(filter: {isCurrent: true}) {
          id
          purchasedAt
          purchasePackage {
            id
            uniqueCode
            __typename
          }
          __typename
        }
        __typename
      }
      currentUserChildEnrolment {
        id
        isTrial
        isActive
        purchases(filter: {isCurrent: true}) {
          id
          purchasedAt
          purchasePackage {
            id
            uniqueCode
            __typename
          }
          __typename
        }
        __typename
      }
      examBoardSubject {
        board
        boardName
        subject
        subjectName
        subjectObject {
          qualification {
            uniqueCode
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    activity(uniqueCode: $activityUniqueCode) {
      id
      __typename
      uniqueCode
      ... on ArticleLesson {
        title
        extraContent
        __typename
      }
      ... on VideoLesson {
        eligibleForV2
        __typename
      }
      ... on ProgressQuizGroup {
        eligibleForV2
        __typename
      }
    }
    __typename
  }
}
